import React, { useEffect, useState } from 'react';
import Axios from 'axios'
import '../App.css'
import { useHistory } from "react-router-dom";

function EditDev() {
     
    let history = useHistory();

    const [dev, setDev] = useState({
        id: '',
        name: '',
        level: '',
        description: ''
    })

    const [levels, setLevels] = useState([

    ])

    /*
    const levels = [
        { value: 'Jedi', label: 'Jedi' },
        { value: 'Sith', label: 'Sith' },
        { value: 'Padawan', label: 'Padawan' },
        { value: 'Jedi Master', label: 'Jedi Master' },
        { value: 'Sith Lord', label: 'Sith Lord' },
        { value: 'Master', label: 'Master' },
        { value: 'Apprentice', label: 'Apprentice' },
        { value: 'Jedi Knight', label: 'Jedi Knight' },
        { value: 'Sith Apprentice', label: 'Sith Apprentice' },
        { value: 'Jedi Padawan', label: 'Jedi Padawan' },
    ]
    */

    useEffect(() => {
        const id = window.location.href.split('/')[5]
        Axios.get('http://localhost:3002/api/dev/' + id)
            .then(response => {
                setDev(response.data[0])
                Axios.get('http://localhost:3002/api/list/level')
                .then(response => {

                    //foreach para setar o valor do select
                    levels.forEach(level => {
                        if(level.value === response.data[0].level){
                            setDev({...dev, level: level.value})
                        }
                    })

                })
            })
        
    }, [])
    


    function handleChange(event) {
        setDev({
            ...dev,
            [event.target.name]: event.target.value
        })
    }

    function handleSubmit(event) {
        event.preventDefault()
        console.log(dev)
        const id = window.location.href.split('/')[5]
        Axios.post('http://localhost:3002/api/edit/dev/'+id, 
            { name: dev.name, level: dev.level, description: dev.description }
        )
        history.push("/")
    } 



    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Nome do desenvolvedor:</label>
                    <input className="form-control" type="text" name="name" value={dev.name} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Nível:</label>
                    <select className="form-control" name="level" value={dev.level} onChange={handleChange}>
                        {
                            levels.map(level => {
                                return <option key={level.value} value={level.value}>{level.label}</option>
                            })
                        }
                    </select>
                </div>
                <div className="form-group">
                    <label>Descrição:</label>
                    <input className="form-control" type="text" name="description" value={dev.description} onChange={handleChange} />
                </div>
                <input className="btn btn-success right" type="submit" value="Editar desenvolvedor" />
            </form>
        </div>
    )
}

export default EditDev
